import React from 'react';
import { Container, Row, Col, Image } from 'react-bootstrap';
// import './About.css';

function About() {
  return (
    <section className="about-section py-5">
      <Container>
        <Row className="align-items-center">
          {/* Left Side: Image */}
          <Col md={6} sm={12}>
            <img src="./images/aboutimage.jpg" alt="About Travel" className="img-fluid rounded" />  
          </Col>

          {/* Right Side: Text */}
          <Col md={6} sm={12}>
            <div className="about-content">
              <h2>Aboutdgdgfdg <span>WanderWorld</span></h2>
              <p>
                Welcome to <strong>WanderWorld</strong> — your travel companion for discovering
                breathtaking destinations across the globe. We believe that travel is not just
                about visiting places; it’s about creating stories, meeting people, and finding
                yourself along the way.
              </p>
              <p>
                Our mission is to inspire travelers like you with authentic experiences, travel tips,
                and guides to help you explore both hidden gems and world-famous landmarks.
              </p>
              <p>
                Whether you’re chasing sunsets in Bali, exploring the streets of Paris, or hiking
                the Swiss Alps — we’re here to make your journey unforgettable.
              </p>
            </div>
          </Col>
        </Row>
      </Container>
    </section>
  );
}

export default About;
