import React from "react";
import { Container, Row, Col, Card, Button } from "react-bootstrap";

function Home() {
  const destinations = [
    {
      title: "Paris, France",
      image: "/images/paris.jpg",
      text: "Experience romance and timeless architecture in the heart of France.",
    },
    {
      title: "Bali, Indonesia",
      image: "/images/Bali.jpg",
      text: "Relax on tropical beaches and explore vibrant island culture.",
    },
    {
      title: "Tokyo, Japan",
      image: "/images/Tokyo.jpg",
      text: "Discover the perfect blend of tradition and modern innovation.",
    },
    {
      title: "New York, USA",
      image: "/images/New",
      text: "Feel the energy of the city that never sleeps.",
    },
  ];

  return (
    <div style={{ backgroundColor: "#f8fafc" }}>
      {/* Hero Section */}
      <div
        style={{
          backgroundImage: "url('/images/hero-travel.jpg')",
          backgroundSize: "cover",
          backgroundPosition: "center",
          height: "85vh",
          display: "flex",
          alignItems: "center",
          justifyContent: "center",
          color: "white",
          textAlign: "center",
          flexDirection: "column",
          textShadow: "2px 2px 10px rgba(0,0,0,0.5)",
        }}
      >
        <h1 style={{ fontSize: "3.5rem", fontWeight: "700" }}>Discover the World</h1>
        <p style={{ fontSize: "1.3rem", maxWidth: "600px", marginTop: "15px" }}>
          Your guide to breathtaking destinations, travel tips, and cultural stories from around the globe.
        </p>
        <Button
          href="/gallery"
          variant="light"
          style={{
            marginTop: "25px",
            fontWeight: "600",
            borderRadius: "30px",
            padding: "10px 25px",
          }}
        >
          Explore Destinations
        </Button>
      </div>

      {/* Featured Destinations */}
      <Container className="py-5">
        <h2 className="text-center mb-4" style={{ fontWeight: "700" }}>
          Featured Destinations
        </h2>
        <Row className="g-4">
          {destinations.map((place, index) => (
            <Col md={3} sm={6} xs={12} key={index}>
              <Card
                className="h-100 shadow-sm border-0"
                style={{ borderRadius: "15px", overflow: "hidden" }}
              >
                <Card.Img
                  variant="top"
                  src={place.image}
                  style={{ height: "220px", objectFit: "cover" }}
                />
                <Card.Body>
                  <Card.Title>{place.title}</Card.Title>
                  <Card.Text style={{ color: "#555", fontSize: "0.95rem" }}>
                    {place.text}
                  </Card.Text>
                </Card.Body>
              </Card>
            </Col>
          ))}
        </Row>
      </Container>

      {/* About / Explore Section */}
      <Container
        fluid
        className="py-5"
        style={{ backgroundColor: "#fff", textAlign: "center" }}
      >
        <h2 style={{ fontWeight: "700" }}>Explore. Inspire. Travel.</h2>
        <p style={{ maxWidth: "700px", margin: "20px auto", color: "#555" }}>
          At <strong>Travel Blog</strong>, we believe that travel opens hearts and broadens minds. 
          From hidden waterfalls to bustling streets, every journey has a story to tell. 
          Let’s explore the beauty of our world together — one adventure at a time.
        </p>
        <Button
          href="/about"
          variant="dark"
          style={{
            fontWeight: "600",
            borderRadius: "30px",
            padding: "10px 25px",
          }}
        >
          Learn More About Us
        </Button>
      </Container>
    </div>
  );
}

export default Home;
