import React from "react";
import Uphead from "./components/Uphead";
import Downfooter from "./components/Downfooter";
import Blogcard from "./components/Blogcard";
import { Container, Row } from "react-bootstrap";
import "./App.css";

function App() {
  const blogs = [
    {
      title: "Paris, France",
      description: "Experience the charm of the Eiffel Tower and romantic vibes of Paris.",
      image: "./images/paris.jpg"
    },
    {
      title: "Tokyo, Japan",
      description: "Explore neon streets, sushi, and technology in Tokyo.",
      image: "./images/tokyo.jpg"
    },
    {
      title: "Bali, Indonesia",
      description: "Tropical beaches, waterfalls, and calm temples await you in Bali.",
      image: "./images/bali.jpg"
    }
  ];

  return (
    <>
      <Uphead />
      <Container className="mt-4">
        <h2 className="text-center mb-4">Popular Destinations</h2>
        <Row className="justify-content-center">
          {blogs.map((blog, index) => (
            <Blogcard
              key={index}
              image={blog.image}
              title={blog.title}
              description={blog.description}
            />
          ))}
        </Row>
      </Container>
      <Downfooter />
    </>
  );
}

export default App;
